<template>
    <div class="sidebar-menu">
        <div :class="{'sidebar-logo-container':true,'collapse':sidebarOpen}">
            <a>
                <img class="sidebar-logo"
                    src="data:image/svg+xml;base64,"
                    alt="logo">
                <h1>Woolinte Serve</h1>
            </a>
        </div>
        <el-scrollbar wrap-class="scrollbar-wrapper" class="scrollbar">
            <el-menu router :default-active="activeMenu" :collapse="sidebarOpen" background-color="#fff" text-color="#000000a6" active-text-color="#409eff" class="el-menu-vertical">
                <sidebar-items v-for="route in routes" :key="route.path" :item="route" :base-path="route.path"></sidebar-items>
            </el-menu>
        </el-scrollbar>
    </div>
</template>

<script>
    import { mapGetters } from "vuex";
    import SidebarItems from "./SidebarItems";

    export default {
        name: "Sidebar",
        data() {
            return {};
        },
        components: {
            SidebarItems
        },
        computed: {
            ...mapGetters(["sidebarOpen"]),
            routes() {
                return this.$router.options.routes;
            },
            activeMenu() {
                const route = this.$route;
                const { meta, path } = route;
                // if set path, the sidebar will highlight the path you set
                if (meta.activeMenu) {
                    return meta.activeMenu;
                }

                return path;
            }
        }
    };
</script>

